import React from "react";
import "./assets/css/bootstrap.min.css";
import "./assets/css/now-ui-kit.css";
import { Button, Badge, Modal } from "reactstrap";

const Link = props => (
  <tr>
    <td>
      <Badge color='info' href={props.link.content} target='_blank'>
        {props.link.content}
      </Badge>
    </td>
    <td>
      <Button color='danger' size='sm' onClick={props.onDelete}>
        Delete
      </Button>
    </td>
  </tr>
);

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      links: []
    };
    this.show();
  }

  show() {
    let headers = new Headers();

    headers.append("Content-Type", "application/json");
    headers.append("Accept", "application/json");
    headers.append("Origin", "http://localhost:3000");

    fetch("http://localhost:8080/saves/get", {
      mode: "cors",
      method: "GET",
      headers: headers
    })
      .then(response => response.json())
      .then(response =>
        this.setState({
          links: response
        })
      )
      .catch(error => console.log("error", error));
  }

  add = () => {
    let headers = new Headers();

    const title = prompt("Title");
    const content = prompt("Content");

    let link = JSON.stringify({ title: title, content: content });

    headers.append("Content-Type", "application/json");
    headers.append("Accept", "application/json");
    headers.append("Origin", "http://localhost:3000");

    fetch("http://localhost:8080/saves/add", {
      mode: "cors",
      method: "POST",
      headers: headers,
      body: link
    })
      .then(response => response.json())
      .then(response =>
        this.setState({
          links: [...this.state.links, response]
        })
      )
      .catch(error => console.log("error", error));
    this.setModalLive(false);
  };

  remove(id) {
    let headers = new Headers();

    headers.append("Content-Type", "application/json");
    headers.append("Accept", "application/json");
    headers.append("Origin", "http://localhost:3000");

    fetch("http://localhost:8080/saves/delete/" + id, {
      mode: "cors",
      method: "POST",
      headers: headers
    }).catch(error => console.log("error", error));

    this.setState({
      links: this.state.links.filter(link => link.id !== id)
    });
  }

  render() {
    const [modalLive, setModalLive] = React.useState(false);
    return (
      <div className='link-list'>
        <table>
          <thead>
            <th>Link</th>
            <th>Action</th>
          </thead>
          <tbody>
            {this.state.links.map(alink => (
              <Link
                key={alink.id}
                onDelete={() => this.remove(alink.id)}
                link={alink}
              ></Link>
            ))}
          </tbody>
        </table>
        <Button color='default' size='md' onClick={() => this.add()}>
          Add
        </Button>

        <Modal toggle={() => setModalLive(false)} isOpen={modalLive}>
          <div className='modal-header'>
            <h5 className='modal-title' id='exampleModalLiveLabel'>
              Add new Link
            </h5>
            <button
              aria-label='Close'
              className='close'
              type='button'
              onClick={() => setModalLive(false)}
            >
              <span aria-hidden={true}>Ã—</span>
            </button>
          </div>
          <div className='modal-body'>
            <p>Woohoo, you're reading this text in a modal!</p>
          </div>
          <div className='modal-footer'>
            <Button
              color='secondary'
              type='button'
              onClick={() => setModalLive(false)}
            >
              Close
            </Button>
            <Button color='primary' type='button' onClick={() => this.add()}>
              Add
            </Button>
          </div>
        </Modal>
      </div>
    );
  }
}

export default App;
